package javaBasics;

public class OperatorExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b;
		System.out.println("Value of a before increment " + a);
		b = a;
		++b;
		b++;
		System.out.println("Value of b after a increment " + b);

	}

}
